import React from "react";
import styled from "styled-components";

type Props = {
  onConfirm: () => void;
  content: string;
  borderRadius?: string;
  fullSize?: boolean;
  size?: string;
  styles?: object;
  dataTest?: string;
};

const Button = styled.div`
    transition: 0.3s ease-in-out;
    cursor: pointer;
    width: 100%;
    background-color: #30b9d6;
    padding: 12px;
    border-radius: 10px;
    display: flex;
    justify-content: center;
    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);

    &:hover {
      box-shadow: 0px 3px 5px rgba(48, 185, 215, 0.3);
    }

    &:active {
      background: rgba(48, 185, 215, 0.8);
    }
  `;

const Text = styled.p`
  color: white;
  user-select: none;
  -moz-user-select: none;
  -khtml-user-select: none;
  -webkit-user-select: none;
  -o-user-select: none;
  
  /* Phone */
  @media screen and (max-width: 768px) {
     
  }  
    
  /* Tablet */
  @media screen and (max-width: 1024px) and (min-width: 768px) {
      font-size: 18px;
  }
`;



const ConfirmButton: React.FC<Props> = (props: Props) => {
  return (
    <Button
      className="confirm-button"
      data-test={props.dataTest}
      {/*
       // @ts-ignore */  ...{}}
      style={{ ...props.styles?.button, borderRadius: `${props.borderRadius ?? "10px"}` }}
      onClick={(e) => props.onConfirm()}
    >
      {/*
       // @ts-ignore */}
      <Text style={props.styles?.text}>{props.content}</Text>
    </Button>
  );
};

export default ConfirmButton;
